package com.yakov_fain.lections.unit11.lesson20;public class TestThreads3 {    public static void main(String args[]) {        TestThreads3 threads3;        MarketNews3 mn = new MarketNews3("Market News");        mn.start();        Portfolio3 p = new Portfolio3("Portfolio data");        p.start();        synchronized (threads3 = new TestThreads3()) {            try {                threads3.wait(15000);            } catch (InterruptedException e) {                System.out.println(e.getMessage());            }        }        System.out.println("The main method of TestThreads3 is finished");    }}